mapper.insert:
INSERT INTO `infrMember` (
	ifmmId
	,ifmmName
	,ifmmPassword
	,ifmmEmail
	,ifmmDomain
	,ifmmMobile
	,ifmmCarrier
	,ifmmRegDateTime
	,ifmmModDateTime
	,ifmmType	
) VALUES (
	#{ifmmId}
	,#{ifmmName}
	,#{ifmmPassword}
	,#{ifmmEmail}
	,(select ifnull(ifcdSeq, 31) from infraCode where ifcdNameEng = #{ifmmDomain})
	,#{ifmmMobile}
	,#{ifmmCarrier}
	,now()
	,now()
	,1
)

